'use client'
import React, { useState } from 'react'
import { cn } from '../../utils/cn'
import { CircleAlert, CircleCheckBig, CircleX, Info, X } from 'lucide-react'

export const Alert = ({
  type = 'info',
  children,
  closeable = true,
  onClose,
  className = '',
  icon,
  ...rest
}) => {
  const [isVisible, setIsVisible] = useState(true)

  const handleClose = () => {
    setIsVisible(false)
    if (onClose) {
      onClose()
    }
  }

  if (!isVisible) return null

  const getAlertStyles = () => {
    const baseStyles = `px-4 py-3 rounded-sm relative`
    switch (type) {
      case 'success':
        return `${baseStyles} bg-success border border-border text-text`
      case 'warning':
        return `${baseStyles} bg-warning border border-border text-text`
      case 'error':
        return `${baseStyles} bg-error border border-border text-text`
      case 'info':
        return `${baseStyles} bg-info border border-border text-text`
      default:
        return baseStyles
    }
  }

  const getIconStyles = () => {
    switch (type) {
      case 'success':
        return 'text-success-foreground'
      case 'warning':
        return 'text-warning-foreground'
      case 'error':
        return 'text-error-foreground'
      case 'info':
        return 'text-info-foreground'
      default:
        return ''
    }
  }
  const getCloseStyles = () => {
    switch (type) {
      case 'success':
        return 'text-success-foreground'
      case 'warning':
        return 'text-warning-foreground'
      case 'error':
        return 'text-error-foreground'
      case 'info':
        return 'text-info-foreground'
      default:
        return ''
    }
  }

  const getIcon = () => {
    switch (type) {
      case 'success':
        return <CircleCheckBig />
      case 'warning':
        return <CircleAlert />
      case 'error':
        return <CircleX />
      case 'info':
        return <Info />
      default:
        return <></>
    }
  }

  return (
    <div className={cn(getAlertStyles(), className)} role='alert' {...rest}>
      <div className='flex items-center'>
        <span className={`mr-2 ${getIconStyles()}`}>{icon || getIcon()}</span>
        <span className='block flex-1'>{children}</span>
      </div>
      {closeable && (
        <span
          className='absolute bottom-0 right-0 top-0 px-4 py-3'
          onClick={handleClose}
          role='button'
          title='Close'
          aria-label='Close'
        >
          <X className={cn(getCloseStyles())} />
        </span>
      )}
    </div>
  )
}

import React from 'react'
import { cn } from '../../utils/cn'
import { AvatarImage } from './AvatarImage'

export const Avatar = ({ className = '', children, onClick, ...rest }) => {
  return (
    <div
      className={cn(
        'bg-bg text-text relative flex size-12 items-center justify-center rounded-full',
        className
      )}
      onClick={onClick}
      {...rest}
    >
      {children && children}
      {!children && <AvatarImage />}
    </div>
  )
}

import { cn } from '../../utils/cn'

export const AvatarBadge = ({
  className = '',
  type = 'status',
  status = 'success',
  counter,
  position = 'bottom-right',
  icon,
  iconEnabled,
  iconSize = 6,
  ...rest
}) => {
  const getPosition = position => {
    if (position === 'bottom-left') {
      return 'bottom-0 left-0'
    } else if (position === 'bottom-right') {
      return 'bottom-0 right-0'
    } else if (position === 'top-left') {
      return 'top-0 left-0'
    } else if (position === 'top-right') {
      return 'top-0 right-0'
    }
    return 'bottom-0 right-0'
  }

  const getStatusColor = status => {
    switch (status) {
      case 'success':
        return 'bg-success-foreground text-success'
      case 'warning':
        return 'bg-warning-foreground text-warning'
      case 'error':
        return 'bg-error-foreground text-error'
      case 'info':
        return 'bg-info-foreground text-info'
      case 'active':
        return 'bg-success-foreground text-primary'
      case 'away':
        return 'bg-warning-foreground text-warning'
      case 'notDisturb':
        return 'bg-error-foreground text-error'
      case 'invisible':
        return 'bg-gray-200 dark:bg-gray-800 text-gray-800 dark:text-gray-200'
      default:
        return 'bg-success-foreground text-success'
    }
  }

  if (type === 'status') {
    return (
      <div
        className={cn(
          'absolute flex size-3 items-center justify-center rounded-full',
          getStatusColor(status),
          getPosition(position),
          className
        )}
        {...rest}
      >
        {iconEnabled && icon && icon}
        {iconEnabled && !icon && (
          <svg
            width={iconSize}
            height={iconSize}
            viewBox='0 0 24 24'
            fill='none'
            xmlns='http://www.w3.org/2000/svg'
          >
            <circle cx='12' cy='12' r='11' fill='currentColor' />
          </svg>
        )}
      </div>
    )
  }
  if (type === 'counter') {
    return (
      <div
        className={cn(
          'absolute flex size-4 items-center justify-center rounded-full text-[12px]',
          getStatusColor(status),
          getPosition(position),
          className
        )}
        {...rest}
      >
        {counter}
      </div>
    )
  }
}

'use client'

import React from 'react'
import { cn } from '../../utils/cn'
import { Avatar } from './Avatar'

export const AvatarGroup = ({ className = '', children, ...rest }) => {
  return (
    <div className={cn('flex items-center -space-x-2', className)} {...rest}>
      {!children ? (
        <>
          <Avatar />
          <Avatar />
        </>
      ) : (
        <>{children}</>
      )}
    </div>
  )
}

'use client'

import { User } from 'lucide-react'
import React from 'react'
import { cn } from '../../utils/cn'

export const AvatarImage = ({
  className = '',
  name,
  src,
  altText,
  ...rest
}) => {
  let nameX

  if (name !== undefined) {
    if (name.split(' ').length > 1) {
      nameX = `${name.split(' ')[0][0]}${name.split(' ')[1][0]}`
    } else {
      nameX = `${name[0]}`
    }
  }

  if (src) {
    return (
      <img
        src={src}
        alt={altText || 'alt text'}
        {...(name && { title: name })}
        className={cn(
          'aspect-square h-auto max-w-full overflow-hidden rounded-full object-cover',
          className
        )}
        {...rest}
      />
    )
  }
  if (!src && name) {
    return (
      <span className={className} title={name} {...rest}>
        {nameX || 'A'}
      </span>
    )
  }
  return (
    <>
      <User />
    </>
  )
}

