import { useEffect, useState } from 'react'

const Star = ({
  className = '',
  size = 24,
  icon,
  style,
  fillPercentage = 100,
  unratedColor,
  onClick,
  onMouseEnter,
  onMouseLeave,
  ...rest
}) => {
  if (icon) {
    return (
      <span
        className={className}
        style={{
          display: 'inline-flex',
          position: 'relative',
          ...style
        }}
        onClick={onClick}
        onMouseEnter={onMouseEnter}
        onMouseLeave={onMouseLeave}
        {...rest}
      >
        <span
          style={{
            position: 'absolute',
            color: `${fillPercentage < 100 ? unratedColor : style.color}`
          }}
        >
          {icon}
        </span>
        <span
          style={{
            clipPath: `inset(0 ${100 - fillPercentage}% 0 0)`
          }}
        >
          {icon}
        </span>
      </span>
    )
  }

  return (
    <span
      onClick={onClick}
      onMouseEnter={onMouseEnter}
      onMouseLeave={onMouseLeave}
      style={{
        display: 'inline-flex',
        position: 'relative',
        ...style
      }}
      className={className}
      {...rest}
    >
      {/* Background star (unfilled) */}
      <svg
        width={size}
        height={size}
        viewBox='0 0 24 22'
        fill='none'
        xmlns='http://www.w3.org/2000/svg'
        style={{
