'use client'

import { Trash2 } from 'lucide-react'
import { useRef, useState } from 'react'
import { cn } from '../../utils/cn'

export const FileUpload = ({
  onFileSelect,
  accept = '*',
  multiple = false,
  maxFiles = 1,
  maxFileSize,
  selectedFiles = [],
  content,
  uploadIcon,
  deleteButton,
  uploadIconClassName = '',
  deleteIconClassName = '',
  ...rest
}) => {
  const [dragActive, setDragActive] = useState(false)
  const [files, setFiles] = useState(selectedFiles)
  const [error, setError] = useState('')
  const inputRef = useRef(null)

  const validateFile = file => {
    if (maxFileSize && file.size > maxFileSize * 1024 * 1024) {
      setError(`File size exceeds ${maxFileSize}MB limit`)
      return false
    }

    if (accept !== '*') {
      const acceptedTypes = accept.split(',').map(type => type.trim())
      const fileType = file.type || `/*.${file.name.split('.').pop()}`
      if (
        !acceptedTypes.some(type =>
          fileType.match(new RegExp(type.replace('*', '.*')))
        )
      ) {
        setError(`File type not accepted. Accepted types: ${accept}`)
        return false
      }
    }

    return true
  }

  const handleFiles = newFiles => {
    setError('')
    const validFiles = []

    for (let i = 0; i < newFiles.length; i++) {
      if (files.length + validFiles.length >= maxFiles) {
        setError(`Maximum ${maxFiles} files allowed`)
        break
      }

      if (validateFile(newFiles[i])) {
        validFiles.push(newFiles[i])
      }
    }

    if (validFiles.length > 0) {
      const updatedFiles = multiple ? [...files, ...validFiles] : validFiles
      setFiles(updatedFiles)
      onFileSelect(updatedFiles)
    }
  }

  const handleChange = e => {
    e.preventDefault()
    if (e.target.files) {
      handleFiles(e.target.files)
    }
  }

  const handleDrop = e => {
    e.preventDefault()
    e.stopPropagation()
    setDragActive(false)
    if (e.dataTransfer.files) {
      handleFiles(e.dataTransfer.files)
    }
  }

  const handleDrag = e => {
    e.preventDefault()
    e.stopPropagation()
    if (e.type === 'dragenter' || e.type === 'dragover') {
      setDragActive(true)
    } else if (e.type === 'dragleave') {
      setDragActive(false)
    }
  }

  const removeFile = indexToRemove => {
    const updatedFiles = files.filter((_, index) => index !== indexToRemove)
    setFiles(updatedFiles)
    onFileSelect(updatedFiles)
  }

  const onButtonClick = () => {
    inputRef.current?.click()
  }

  return (
    <div className='w-full' {...rest}>
      <div
        className={`border-border hover:bg-bg-light/50 flex h-64 w-full cursor-pointer flex-col items-center justify-center rounded-lg border border-dashed transition-all duration-200 ${
          dragActive ? 'bg-bg-light/50' : ''
        }`}
        onDragEnter={handleDrag}
        onDragLeave={handleDrag}
        onDragOver={handleDrag}
        onDrop={handleDrop}
        onClick={onButtonClick}
      >
        <input
          ref={inputRef}
          type='file'
          className='hidden'
          multiple={multiple}
          accept={accept}
          onChange={handleChange}
        />
        {!content && (
          <>
            {uploadIcon && (
              <span className={cn('', uploadIconClassName)}>{uploadIcon}</span>
            )}
            {!uploadIcon && (
              <svg
                className={cn('text-text mb-3 h-10 w-10', uploadIconClassName)}
                fill='none'
                stroke='currentColor'
                viewBox='0 0 24 24'
                xmlns='http://www.w3.org/2000/svg'
              >
                <path
                  strokeLinecap='round'
                  strokeLinejoin='round'
                  strokeWidth='2'
                  d='M7 16a4 4 0 01-.88-7.903A5 5 0 1115.9 6L16 6a5 5 0 011 9.9M15 13l-3-3m0 0l-3 3m3-3v12'
                ></path>
              </svg>
            )}
            <p className='text-body1 text-text mb-2'>
              <span className='font-semibold'>Click to upload</span> or drag and
              drop
            </p>
            <p className='text-text-muted text-xs'>
              {multiple ? `Upload up to ${maxFiles} files` : 'Upload a file'}
              {maxFileSize && ` (Max size: ${maxFileSize}MB)`}
            </p>
          </>
        )}
        {error && <p className='text-error-500 mt-2 text-xs'>{error}</p>}
      </div>

      {/* Selected Files List */}
      {files.length > 0 && (
        <div className='mt-4'>
          <h4 className='text-text mb-2 text-sm font-medium'>
            Selected Files:
          </h4>
          <ul className='space-y-2'>
            {files.map((file, index) => (
              <li
                key={`${file.name}-${index}`}
                className='bg-bg-light text-text flex items-center justify-between rounded-lg p-2 px-4'
              >
                <span className='text-sm'>{file.name}</span>
                <button
                  onClick={e => {
                    e.stopPropagation()
                    removeFile(index)
                  }}
                  className='text-error'
                >
                  {deleteButton ? (
                    deleteButton
                  ) : (
                    <Trash2 className={cn('h-4 w-4', deleteIconClassName)} />
                  )}
                </button>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  )
}

